mapper.select:

			SELECT UUID
            FROM VISITOR_MEMBER
            WHERE UUID= #{uuid, jdbcType=VARCHAR}


			SELECT SITECODE,X_LOCATION,Y_LOCATION
            FROM VISITOR_LOCATION
            WHERE SITECODE = #{sitecode, jdbcType=VARCHAR}

mapper.update:

UPDATE VISITOR_MEMBER				
SET NAME = #{NAME}
	,PHONE = #{PHONE}
	,ADDRESS = #{ADDRESS}
WHERE ID = #{ID}

mapper.select:
SELECT COUNT(*) OVER() AS TOTAL_COUNT, VF.UUID ,substr(VM.NAME,1,1) || lpad('*',length(VM.NAME)-1,'*') AS NAME ,VM.RESPONSE_NUM ,VM.NATIONAL_INFO ,DECODE(VM.SEX, '1', '남자', '0', '여자', '기타') SEX ,VM.BIRTH ,VM.AGENCY ,substr(VM.PHONE,1,7) || '****' AS PHONE ,VL.SITECODE ,substr(VL.GIGWAN_NAME,1,1) || lpad('*',length(VL.GIGWAN_NAME)-1,'*') AS GIGWAN_NAME ,VL.ADDRESS_NAME ,VL.PHONE_NO ,TO_CHAR(VF.INS_DATE, 'YYYY.MM.DD HH24:MI:SS') INS_DATE FROM VISITOR_FOOOTPRINT VF,VISITOR_MEMBER VM,VISITOR_LOCATION VL WHERE VF.UUID = VM.UUID AND VF.SITECODE = VL.SITECODE
AND VM.NAME LIKE '%' || #{name} ||'%'
AND VL.GIGWAN_NAME LIKE '%' || #{gigwan_name} ||'%'
AND TO_CHAR(VF.ins_date,'YYYYMMDD') >= #{from_date}
AND TO_CHAR(VF.ins_date,'YYYYMMDD') <= #{to_date}
ORDER BY VF.ins_date DESC

SELECT ROWNUM AS RNUM, AA.* FROM(
SELECT COUNT(*) OVER() AS TOTAL_COUNT, VF.UUID ,substr(VM.NAME,1,1) || lpad('*',length(VM.NAME)-1,'*') AS NAME ,VM.RESPONSE_NUM ,VM.NATIONAL_INFO ,DECODE(VM.SEX, '1', '남자', '0', '여자', '기타') SEX ,VM.BIRTH ,VM.AGENCY ,substr(VM.PHONE,1,7) || '****' AS PHONE ,VL.SITECODE ,substr(VL.GIGWAN_NAME,1,1) || lpad('*',length(VL.GIGWAN_NAME)-1,'*') AS GIGWAN_NAME ,VL.ADDRESS_NAME ,VL.PHONE_NO ,TO_CHAR(VF.INS_DATE, 'YYYY.MM.DD HH24:MI:SS') INS_DATE FROM VISITOR_FOOOTPRINT VF,VISITOR_MEMBER VM,VISITOR_LOCATION VL WHERE VF.UUID = VM.UUID AND VF.SITECODE = VL.SITECODE
AND VM.NAME LIKE '%' || #{name} ||'%'
AND SEX = #{sex_gb}
AND TO_CHAR(VF.ins_date,'YYYYMMDD') >= #{from_date}
AND TO_CHAR(VF.ins_date,'YYYYMMDD') <= #{to_date}
ORDER BY VF.ins_date DESC )AA
