mapper.select:
SELECT UUID ,NAME ,BIRTH ,PHONE ,DONG ,AGREE ,GUBUN_CODE ,GUBUN_NAME ,CI ,TO_CHAR(INS_DATE,'YYYY-MM-DD HH24:MI:SS') INS_DATE ,UPD_ID ,UPD_DATE ,DEL_ID ,DEL_DATE ,CONFIRM_YN ,SEX ,DEL_YN ,ADDRESS ,DEL_ID ,CONFIRM_YN FROM WELFARE_SALARY WHERE
CI = #{ci}
UUID = #{uuid}
AND NAME = #{name} AND DONG = #{deptName} ORDER BY INS_DATE DESC

SELECT COUNT(*) OVER() AS TOTAL_COUNT ,UUID ,NAME ,BIRTH ,PHONE ,DONG ,AGREE ,GUBUN_CODE ,GUBUN_NAME ,CI ,TO_CHAR(INS_DATE,'YYYY-MM-DD HH24:MI:SS') INS_DATE ,UPD_ID ,UPD_DATE ,DEL_ID ,DEL_DATE ,CONFIRM_YN ,SEX ,DEL_YN ,ADDRESS ,DEL_ID ,CONFIRM_YN FROM WELFARE_SALARY
WHERE DONG = #{deptName}
ORDER BY INS_DATE DESC
